<script type="text/javascript" src="<?=base_url()?>js/scrolltop/modernizr.custom.11333.js"></script>
<script type="text/javascript" src="<?=base_url()?>js/scrolltop/jquery.easing.1.3.js"></script>
        <style>.ss-container{display:one;position:absolute}      
            #ss-links{	position: fixed;	right: 30px;	bottom: 10px;	z-index: 100;}#ss-links a{	background: rgba(0,0,0,0.4);	font-size: 16px;	width: 50px;	height: 40px;	background-image:url(<?=base_url()?>images/system/arrowup.png);	background-repeat: no-repeat;	background-position: center; 	line-height: 40px;	margin: 5px;	float: left;	border-top-left-radius: 30%;	border-top-right-radius: 30%;	display: block;	text-align: center;	-webkit-transition: background 0.2s linear;	-moz-transition: background 0.2s linear;	-o-transition: background 0.2s linear;	-ms-transition: background 0.2s linear;	transition: background 0.2s linear;outline:none;}#ss-links a:hover{	background: rgba(0,0,0,0.8) url(<?=base_url()?>images/system/arrowup.png);	background-repeat: no-repeat;	background-position: center; }</style>
  			<div id="ss-links" class="ss-links" >
  				<a href="#top"></a>
  			</div>
        <div id="ss-container" class="ss-container">
          <h2 id="top"></h2>
        </div>
<script type="text/javascript">$(function() {$(window).scroll(function(){    var aTop = $('.ad').height();    if($(this).scrollTop()>=aTop){     var scrollTop = $(window).scrollTop();    if(scrollTop>50)      {      	$('#ss-links').fadeIn(300);      }    else      {      	$('#ss-links').fadeOut(300);      }    }  });			var $sidescroll	= (function() {				var $rows			= $('#ss-container > div.ss-row'),					$rowsViewport, $rowsOutViewport,					$links			= $('#ss-links > a'),					$win			= $(window),					winSize			= {},					anim			= false,					scollPageSpeed	= 1000 ,					scollPageEasing = 'easeInOutExpo',					hasPerspective	= true,										perspective		= hasPerspective && Modernizr3.csstransforms3d,					init			= function() {						getWinSize();						initEvents();						defineViewport();						setViewportRows();						if( perspective ) {							$rows.css({								'-webkit-perspective'			: 600,								'-webkit-perspective-origin'	: '50% 0%'							});						}						$rowsViewport.find('a.ss-circle').addClass('ss-circle-deco');						placeRows();											},					defineViewport	= function() {											$.extend( $.expr[':'], {													inviewport	: function ( el ) {								if ( $(el).offset().top < winSize.height ) {									return true;								}								return false;							}												});										},					setViewportRows	= function() {												$rowsViewport 		= $rows.filter(':inviewport');						$rowsOutViewport	= $rows.not( $rowsViewport )											},					getWinSize		= function() {											winSize.width	= $win.width();						winSize.height	= $win.height();										},					initEvents		= function() {												$links.on( 'click.Scrolling', function( event ) {														$('html, body').stop().animate({								scrollTop: $( $(this).attr('href') ).offset().top							}, scollPageSpeed, scollPageEasing );							return false;												});												$(window).on({							'resize.Scrolling' : function( event ) {														getWinSize();								setViewportRows();								$rows.find('a.ss-circle').removeClass('ss-circle-deco');								$rowsViewport.each( function() {																	$(this).find('div.ss-left')										   .css({ left   : '0%' })										   .end()										   .find('div.ss-right')										   .css({ right  : '0%' })										   .end()										   .find('a.ss-circle')										   .addClass('ss-circle-deco');																});														},							'scroll.Scrolling' : function( event ) {																if( anim ) return false;								anim = true;								setTimeout( function() {																		placeRows();									anim = false;																	}, 10 );														}						});										},					placeRows		= function() {												var winscroll	= $win.scrollTop(),							winCenter	= winSize.height / 2 + winscroll;						$rowsOutViewport.each( function(i) {														var $row	= $(this),								$rowL	= $row.find('div.ss-left'),								$rowR	= $row.find('div.ss-right'),								rowT	= $row.offset().top;							if( rowT > winSize.height + winscroll ) {																if( perspective ) {																	$rowL.css({										'-webkit-transform'	: 'translate3d(-75%, 0, 0) rotateY(-90deg) translate3d(-75%, 0, 0)',										'opacity'			: 0									});									$rowR.css({										'-webkit-transform'	: 'translate3d(75%, 0, 0) rotateY(90deg) translate3d(75%, 0, 0)',										'opacity'			: 0									});																}								else {																	$rowL.css({ left 		: '-50%' });									$rowR.css({ right 		: '-50%' });																}															}							else {								var rowH	= $row.height(),									factor 	= ( ( ( rowT + rowH / 2 ) - winCenter ) / ( winSize.height / 2 + rowH / 2 ) ),									val		= Math.max( factor * 50, 0 );																	if( val <= 0 ) {									if( !$row.data('pointer') ) {									  $('#ss-links').css('display','none');										$row.data( 'pointer', true );										$row.find('.ss-circle').addClass('ss-circle-deco');																		}																}								else {									if( $row.data('pointer') ) {																					$row.data( 'pointer', false );										$row.find('.ss-circle').removeClass('ss-circle-deco');																		}																	}								if( perspective ) {																		var	t		= Math.max( factor * 75, 0 ),										r		= Math.max( factor * 90, 0 ),										o		= Math.min( Math.abs( factor - 1 ), 1 );																		$rowL.css({										'-webkit-transform'	: 'translate3d(-' + t + '%, 0, 0) rotateY(-' + r + 'deg) translate3d(-' + t + '%, 0, 0)',										'opacity'			: o									});									$rowR.css({										'-webkit-transform'	: 'translate3d(' + t + '%, 0, 0) rotateY(' + r + 'deg) translate3d(' + t + '%, 0, 0)',										'opacity'			: o									});																}								else {																		$rowL.css({ left 	: - val + '%' });									$rowR.css({ right 	: - val + '%' });																	}															}													});										};				return { init : init };						})();			$sidescroll.init();		});</script>
